import React, { Component } from 'react'
import TableList from '@c/TableList
export default class Demo extends Component {
  state = {
    chooseValue: [],
    pageList: {
      page: 1,
      size: 20
    },
    dataList: [
      {
        id: 1,
        name: '张三',
        sex: 1,
      },
      {
        id: 2,
        name: '李四',
        sex: 2,
      },
    ],
  }
  columns = [
    {
      title: '序号',
      dataIndex: 'index',
      width: '10%',
      render: (text: any, record: object, index: number) => {
        return (this.state.pageList.page - 1) * this.state.pageList.size + (index + 1);
      },
    },
    {
      title: '姓名',
      dataIndex: 'name',
      width: '20%',
    },
    {
      title: '年龄',
      dataIndex: 'sex',
      render: (text: string) => {
        return parseInt(text) === 1 ? '男' : '女';
      },
      width: '20%',
    },
    {
      title: '操作',
      key: 'action',
      width: '40%',
      render: (record: object) => (
        <div>
          <Tag onClick={() => this.delList(record)} color="orange">
            删除
          </Tag>
          <Tag onClick={() => this.modify(record)} color="#ccc">
            编辑
          </Tag>
        </div>
      ),
    },
  ]
  render () {
    return (
     <TableList
        type="radio"
        chooseValue = { this.state.chooseValue }
        columns={this.columns}
        dataList={this.state.dataList}
      ></TableList>
    )
  }
}

